mapper.select:
select
from t_item_type itemType where 1 = 1 and deleted = 0
AND name = #{name}

mapper.update:
update t_item_type
name = #{name, jdbcType=VARCHAR}, remark = #{remark, jdbcType=VARCHAR}, status = #{status, jdbcType=INTEGER}, deleted = #{deleted, jdbcType=INTEGER}, create_by = #{createBy, jdbcType=BIGINT}, create_date = #{createDate, jdbcType=TIMESTAMP}, update_by = #{updateBy, jdbcType=BIGINT}, update_date = #{updateDate, jdbcType=TIMESTAMP},
where id = #{id, jdbcType=INTEGER}

update t_item_type
name = #{name, jdbcType=VARCHAR}, remark = #{remark, jdbcType=VARCHAR}, status = #{status, jdbcType=INTEGER}, deleted = #{deleted, jdbcType=INTEGER}, create_by = #{createBy, jdbcType=BIGINT}, create_date = #{createDate, jdbcType=TIMESTAMP}, update_by = #{updateBy, jdbcType=BIGINT}, update_date = #{updateDate, jdbcType=TIMESTAMP}
where id = #{id, jdbcType=INTEGER}
